mapper.select:
  SELECT

from sw_work_detail
where 1=1

  and  work_date >= #{startTime,jdbcType="VARCHAR"}


  and work_date <= #{endTime,jdbcType="VARCHAR"}


  and order_no = #{orderNo,jdbcType="VARCHAR" }


  and user_name = #{userName,jdbcType="VARCHAR" }


  and step_no = #{stepNo,jdbcType="VARCHAR" }
